local hpLoss = 0.04local minHP = 0.4local unFair = falselocal activated = truefunction opponentNoteHit(id, direction, noteType, isSustainNote)if activated == true then --Checks if it is activatedif getProperty('health') > minHP then --Checks if the health is less or equal than the minHP valueif unFair == true then --Checks if the unfair value is truesetProperty('health',getProperty('health')-hpLoss) --HP Loss shitendif unFair == false then --Checks if the unfair value is falseif isSustainNote == false then --Checks if the note is a long note (This only drains health with the actual note, not with the *sustain*)setProperty('health',getProperty('health')-hpLoss) --HP Loss shitendendendendend